      radial-gradient(circle at 80% 20%, hsl(120 100% 50% / 0.03) 0%, transparent 50%),
      radial-gradient(circle at 40% 80%, hsl(180 100% 50% / 0.03) 0%, transparent 50%);
  }

  /* Custom scrollbar styling for GitHub theme */
  ::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  ::-webkit-scrollbar-track {
    background: var(--github-bg);
  }

  ::-webkit-scrollbar-thumb {
    background: var(--github-border);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: var(--github-text-secondary);
  }

  /* Monaco editor overrides */
  .monaco-editor {
    background-color: var(--github-bg) !important;
  }

  .monaco-editor .view-lines {
    font-family: var(--font-code) !important;
  }

  /* Custom utility classes */
  .font-ui {
    font-family: var(--font-ui);
  }

  .font-code {
    font-family: var(--font-code);
  }

  .github-bg {
    background-color: var(--github-bg);
  }

  .github-surface {
    background-color: var(--github-surface);
  }

  .github-text {
    color: var(--github-text);
  }

  .github-text-secondary {
    color: var(--github-text-secondary);
  }

  .github-primary {
    color: var(--github-primary);
  }

  .github-success {
    color: var(--github-success);
  }

  .github-warning {
    color: var(--github-warning);
  }

  .github-error {
    color: var(--github-error);
  }

  .github-border {
    border-color: var(--github-border);
  }

  /* Animation utilities */
  .animate-pulse-slow {
    animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  /* Smooth transitions for theme switching */
  * {
    transition-property: background-color, border-color, color, fill, stroke;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    transition-duration: 200ms;
  }

  /* Focus styles for accessibility */
  button:focus-visible,
  input:focus-visible,
  textarea:focus-visible,
  select:focus-visible {
    outline: 2px solid var(--github-primary);
    outline-offset: 2px;
  }

  /* Code syntax highlighting overrides */
  .hljs {
    background: var(--github-bg) !important;
    color: var(--github-text) !important;
  }

  .hljs-comment {
    color: var(--github-text-secondary) !important;
    font-style: italic;
  }

  .hljs-keyword {
    color: hsl(0 70% 70%) !important;
  }

  .hljs-string {
    color: hsl(195 100% 75%) !important;
  }

  .hljs-number {
    color: hsl(240 100% 75%) !important;
  }

  .hljs-type {
    color: var(--github-warning) !important;
  }

  .hljs-function {
    color: hsl(270 60% 75%) !important;
  }

  .hljs-variable {
    color: var(--github-text) !important;
  }
}

@layer components {
  /* Custom button variants for GitHub theme */
  .btn-github-primary {
    @apply bg-github-primary hover:bg-github-primary/90 text-white border-transparent;
  }

  .btn-github-success {
    @apply bg-github-success hover:bg-github-success/90 text-white border-transparent;
  }

