import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { format } from "date-fns";

export default function Dashboard() {
  const [, navigate] = useLocation();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [projectName, setProjectName] = useState("");
  const [repoUrl, setRepoUrl] = useState("");
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch all projects
  const { data: projects, isLoading } = useQuery({
    queryKey: ['/api/projects'],
  });

  // Create project mutation
  const createProjectMutation = useMutation({
    mutationFn: async (data: { name: string; repoUrl?: string }) => {
      const response = await apiRequest('POST', '/api/projects', {
        name: data.name,
        repoUrl: data.repoUrl || null,
        defaultBranch: 'main',
        settingsJson: {
          strictMode: true,
          maxLines: 1000,
          maxFiles: 50,
          forbiddenGlobs: ['node_modules/**', '.git/**'],
          styleFreeze: false
        }
      });
      return response.json();
    },
    onSuccess: (project) => {
      queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
      setIsCreateDialogOpen(false);
      setProjectName("");
      setRepoUrl("");
      toast({
        title: "Project created",
        description: `${project.name} has been created successfully`
      });
      // Navigate to the new project
      navigate(`/project/${project.id}`);
    },
    onError: (error) => {
      toast({
        title: "Failed to create project",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  const handleCreateProject = () => {
    if (!projectName.trim()) {
      toast({
        title: "Project name required",
        description: "Please enter a name for your project",
        variant: "destructive"
      });
      return;
    }
    createProjectMutation.mutate({ name: projectName, repoUrl });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-github-bg text-github-text flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-github-primary mx-auto mb-4"></div>
          <p>Loading projects...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-github-bg text-github-text">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-4xl font-bold text-github-text mb-2">
              Welcome to <span className="text-github-primary">Reme</span>
            </h1>
            <p className="text-github-text-secondary text-lg">
              A better web IDE with intelligent AI assistance
            </p>
          </div>

          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button className="bg-github-primary hover:bg-github-primary/90 text-white">
                <i className="fas fa-plus mr-2"></i>
                New Project
              </Button>
            </DialogTrigger>
            <DialogContent className="bg-github-surface border-github-border">
              <DialogHeader>
                <DialogTitle className="text-github-text">Create New Project</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="project-name" className="text-github-text">Project Name</Label>
                  <Input
                    id="project-name"
                    value={projectName}
                    onChange={(e) => setProjectName(e.target.value)}
                    placeholder="My Awesome Project"
                    className="bg-github-bg border-github-border text-github-text"
                  />
                </div>
                <div>
                  <Label htmlFor="repo-url" className="text-github-text">Repository URL (Optional)</Label>
                  <Input
                    id="repo-url"
                    value={repoUrl}
                    onChange={(e) => setRepoUrl(e.target.value)}
                    placeholder="https://github.com/user/repo.git"
                    className="bg-github-bg border-github-border text-github-text"
                  />
                  <p className="text-sm text-github-text-secondary mt-1">
                    Leave empty to create a new repository
                  </p>
                </div>
                <Button 
                  onClick={handleCreateProject} 
                  disabled={createProjectMutation.isPending}
                  className="w-full bg-github-primary hover:bg-github-primary/90"
                >
                  {createProjectMutation.isPending ? "Creating..." : "Create Project"}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>

        {/* Projects Grid */}
        {projects && Array.isArray(projects) && projects.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {projects.map((project: any) => (
              <Card 
                key={project.id} 
                className="bg-github-surface border-github-border hover:border-github-primary/50 transition-colors cursor-pointer"
                onClick={() => navigate(`/project/${project.id}`)}
              >
                <CardHeader>
                  <CardTitle className="text-github-text flex items-center justify-between">
                    {project.name}
                    <Badge variant="secondary" className="bg-github-bg">
                      {project.defaultBranch || 'main'}
                    </Badge>
                  </CardTitle>
                  <CardDescription className="text-github-text-secondary">
                    {project.repoUrl ? (
                      <span className="flex items-center">
                        <i className="fas fa-code-branch mr-2"></i>
                        {project.repoUrl}
                      </span>
                    ) : (
                      <span className="flex items-center">
                        <i className="fas fa-folder mr-2"></i>
                        Local project
                      </span>
                    )}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="flex items-center justify-between text-sm text-github-text-secondary">
                    <span>
                      Created {format(new Date(project.createdAt), 'MMM dd, yyyy')}
                    </span>
                    <div className="flex items-center space-x-2">
                      {project.settingsJson?.strictMode && (
                        <Badge variant="outline" className="text-xs">
                          Strict Mode
                        </Badge>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          /* Empty State */
          <div className="text-center py-16">
            <div className="text-6xl text-github-text-secondary mb-4">
              <i className="fas fa-folder-open"></i>
            </div>
            <h2 className="text-2xl font-semibold text-github-text mb-2">No projects yet</h2>
            <p className="text-github-text-secondary mb-6">
              Create your first project to get started with Reme
            </p>
            <Button 
              onClick={() => setIsCreateDialogOpen(true)}
              className="bg-github-primary hover:bg-github-primary/90 text-white"
            >
              <i className="fas fa-plus mr-2"></i>
              Create Your First Project
            </Button>
          </div>
        )}

        {/* Features Section */}
        <div className="mt-16 border-t border-github-border pt-16">
          <h2 className="text-2xl font-semibold text-github-text mb-8 text-center">
            Why Choose Reme?
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="text-4xl text-github-primary mb-4">
                <i className="fas fa-robot"></i>
              </div>
              <h3 className="text-lg font-semibold text-github-text mb-2">Intelligent AI Agent</h3>
              <p className="text-github-text-secondary">
                Strict scope management ensures the AI only touches what you explicitly request
              </p>
            </div>
            <div className="text-center">
              <div className="text-4xl text-github-primary mb-4">
                <i className="fas fa-brain"></i>
              </div>
              <h3 className="text-lg font-semibold text-github-text mb-2">Project Memory</h3>
              <p className="text-github-text-secondary">
                Comprehensive memory system remembers your coding patterns and project history
              </p>
            </div>
            <div className="text-center">
              <div className="text-4xl text-github-primary mb-4">
                <i className="fas fa-code-branch"></i>
              </div>
              <h3 className="text-lg font-semibold text-github-text mb-2">Git Integration</h3>
              <p className="text-github-text-secondary">
                Seamless Git operations with real-time diff visualization and approval workflow
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/ide.tsx.content:
import { useState } from "react";
import { useParams } from "wouter";
import FileExplorer from "@/components/FileExplorer";
import Monaco from "@/components/Monaco";
import AgentPanel from "@/components/AgentPanel";
import DiffPanel from "@/components/DiffPanel";
import MemoryPanel from "@/components/MemoryPanel";
import GitPanel from "@/components/GitPanel";
import Terminal from "@/components/Terminal";
import StatusBar from "@/components/StatusBar";
import { useIDE } from "@/hooks/useIDE";
import { cn } from "@/lib/utils";

export default function IDE() {
  const { id: projectId } = useParams();
  const [activePanel, setActivePanel] = useState("agent");
  const [activeBottomPanel, setActiveBottomPanel] = useState("terminal");
  const [activeSidebarTab, setActiveSidebarTab] = useState("explorer");

  // Redirect to dashboard if no project ID
  if (!projectId) {
    window.location.href = '/';
    return null;
  }

  const {
    currentFile,
    fileContent,
    tabs,
    gitStatus,
    sessions,
    isConnected,
    project,
    openFile,
    closeTab,
    sendMessage,
    commitChanges,
    pullChanges,
    pushChanges
  } = useIDE(projectId || "");

  const sidebarTabs = [
    { id: "explorer", icon: "fas fa-folder", title: "Explorer" },
    { id: "agent", icon: "fas fa-robot", title: "Agent" },
    { id: "git", icon: "fas fa-code-branch", title: "Git" },
    { id: "memory", icon: "fas fa-brain", title: "Memory" },
    { id: "settings", icon: "fas fa-cog", title: "Settings" }
  ];

  const rightPanelTabs = [
    { id: "agent", label: "Agent" },
    { id: "diff", label: "Diff" },
    { id: "memory", label: "Memory" }
  ];

  const bottomPanelTabs = [
    { id: "terminal", label: "Terminal" },
    { id: "git", label: "Git" },
    { id: "tests", label: "Tests" },
    { id: "problems", label: "Problems" }
  ];

  return (
    <div className="flex h-screen bg-github-bg text-github-text font-code">
      {/* Top Navigation Bar */}
      <div className="fixed top-0 left-0 right-0 h-10 bg-github-surface border-b border-github-border flex items-center justify-between px-4 z-50 shadow-sm">
        <div className="flex items-center space-x-4">
          <button 
            onClick={() => window.location.href = '/'}
            className="text-github-text-secondary hover:text-github-primary transition-colors flex items-center space-x-2"
          >
            <i className="fas fa-home text-sm"></i>
            <span className="text-sm font-medium">REME</span>
          </button>
          <div className="text-github-accent text-sm font-mono">
            {(project as any)?.name || 'Loading...'}
          </div>
        </div>
        <div className="flex items-center space-x-3">
          <div className="text-xs text-github-text-secondary font-mono">
            v1.0.0
          </div>
          <div className={cn(
            "px-2 py-1 rounded-md text-xs font-mono border",
            isConnected 
              ? "bg-github-success/10 text-github-success border-github-success/20" 
              : "bg-github-error/10 text-github-error border-github-error/20"
          )}>
            {isConnected ? "â— ONLINE" : "â— OFFLINE"}
          </div>
        </div>
      </div>

      {/* Main Layout */}
      <div className="flex h-full w-full pt-10">
      {/* Sidebar */}
      <div className="flex h-full">
        {/* Activity Bar */}
        <div className="w-12 bg-gradient-to-b from-github-surface to-github-bg border-r border-github-border flex flex-col items-center py-4 space-y-3 shrink-0 shadow-lg">
          {sidebarTabs.map((tab) => (
            <button
              key={tab.id}
              className={cn(
                "w-9 h-9 flex items-center justify-center rounded-lg transition-all duration-200 relative group",
                activeSidebarTab === tab.id
                  ? "text-github-primary bg-github-primary/20 shadow-lg shadow-github-primary/25 border border-github-primary/30"
                  : "text-github-text-secondary hover:text-github-primary hover:bg-github-primary/10 hover:scale-105"
              )}
              title={tab.title}
              onClick={() => setActiveSidebarTab(tab.id)}
            >
              <i className={`${tab.icon} text-sm`}></i>
              {activeSidebarTab === tab.id && (
                <div className="absolute -right-1 top-1/2 transform -translate-y-1/2 w-1 h-6 bg-github-primary rounded-l"></div>
              )}
            </button>
          ))}
        </div>

        {/* Sidebar Panel */}
        <div className="w-80 bg-github-surface border-r border-github-border flex flex-col shrink-0 h-full overflow-hidden shadow-lg">
          {activeSidebarTab === "explorer" && (
            <>
              <div className="px-4 py-3 border-b border-github-border flex items-center justify-between shrink-0 bg-gradient-to-r from-github-surface to-github-bg">
                <div className="flex items-center space-x-2">
                  <i className="fas fa-folder text-github-primary text-sm"></i>
                  <h3 className="text-sm font-bold text-github-primary tracking-wide">EXPLORER</h3>
                </div>
                <div className="flex items-center space-x-2">
                  <button className="text-github-text-secondary hover:text-github-primary transition-colors p-1 rounded hover:bg-github-primary/10">
                    <i className="fas fa-plus text-xs"></i>
                  </button>
                  <button className="text-github-text-secondary hover:text-github-primary transition-colors p-1 rounded hover:bg-github-primary/10">
                    <i className="fas fa-folder-plus text-xs"></i>
                  </button>
                  <button className="text-github-text-secondary hover:text-github-primary transition-colors p-1 rounded hover:bg-github-primary/10">
                    <i className="fas fa-refresh text-xs"></i>
                  </button>
                </div>
              </div>
              <div className="flex-1 overflow-y-auto scrollbar-github">
                <FileExplorer projectId={projectId} onFileSelect={openFile} />
              </div>
            </>
          )}

          {activeSidebarTab === "git" && (
            <>
              <div className="px-4 py-3 border-b border-github-border shrink-0">
                <h3 className="text-sm font-medium text-github-text">SOURCE CONTROL</h3>
              </div>
              <div className="flex-1 overflow-y-auto">
                <GitPanel 
                  projectId={projectId}
                  gitStatus={gitStatus}
                  onCommit={commitChanges}
                  onPull={pullChanges}
                  onPush={pushChanges}
                />
              </div>
            </>
          )}

          {activeSidebarTab === "agent" && (
            <>
              <div className="px-4 py-3 border-b border-github-border shrink-0">
                <h3 className="text-sm font-medium text-github-text">AGENT</h3>
              </div>
              <div className="flex-1 overflow-y-auto">
                <AgentPanel 
                  projectId={projectId}
                  sessions={sessions}
                  isConnected={isConnected}
                  onSendMessage={(message: string) => {
                    sendMessage(message);
                  }}
                />
              </div>
            </>
          )}

          {activeSidebarTab === "memory" && (
            <>
              <div className="px-4 py-3 border-b border-github-border shrink-0">
                <h3 className="text-sm font-medium text-github-text">MEMORY</h3>
              </div>
              <div className="flex-1 overflow-y-auto">
                <MemoryPanel projectId={projectId} />
              </div>
            </>
          )}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Tab Bar */}
        <div className="bg-github-surface border-b border-github-border flex items-center">
          <div className="flex">
            {tabs.map((tab) => (
              <div
                key={tab.path}
                className={cn(
                  "flex items-center px-4 py-2 border-r border-github-border text-sm cursor-pointer",
                  currentFile === tab.path
                    ? "bg-github-bg text-github-text"
                    : "text-github-text-secondary hover:text-github-text hover:bg-github-border/30"
                )}
                onClick={() => openFile(tab.path)}
              >
                <i className={`${tab.icon} mr-2`}></i>
                <span>{tab.name}</span>
                {tab.modified && (
                  <span className="ml-2 w-2 h-2 bg-orange-400 rounded-full" title="Modified"></span>
                )}
                <button 
                  className="ml-2 text-github-text-secondary hover:text-github-text"
                  onClick={(e) => {
                    e.stopPropagation();
                    closeTab(tab.path);
                  }}
                >
                  <i className="fas fa-times text-xs"></i>
                </button>
              </div>
            ))}
          </div>
          <div className="flex-1"></div>
          <div className="px-4 py-2 text-xs text-github-text-secondary">
            <span className="mr-4">Ln 42, Col 16</span>
            <span className="mr-4">UTF-8</span>
            <span>TypeScript React</span>
          </div>
        </div>

        {/* Editor and Right Panel */}
        <div className="flex-1 flex">
          {/* Monaco Editor */}
          <div className="flex-1">
            <Monaco
              value={fileContent}
              language="typescript"
              path={currentFile}
              onChange={(value) => {
                // Handle file content changes
                console.log('File changed:', currentFile, value);
              }}
            />
          </div>

          {/* Right Panel */}
          <div className="w-96 bg-github-surface border-l border-github-border flex flex-col">
            {/* Panel Tabs */}
            <div className="flex border-b border-github-border">
              {rightPanelTabs.map((tab) => (
                <button
                  key={tab.id}
                  className={cn(
                    "flex-1 px-4 py-2 text-sm font-medium transition-colors",
                    activePanel === tab.id
                      ? "text-github-primary bg-github-primary/10 border-b-2 border-github-primary"
                      : "text-github-text-secondary hover:text-github-text"
                  )}
                  onClick={() => setActivePanel(tab.id)}
                >
                  {tab.label}
                </button>
              ))}
            </div>

            {/* Panel Content */}
            <div className="flex-1 overflow-y-auto">
              {activePanel === "agent" && (
                <AgentPanel
                  projectId={projectId}
                  sessions={sessions}
                  isConnected={isConnected}
                  onSendMessage={sendMessage}
                />
              )}
              {activePanel === "diff" && (
                <DiffPanel projectId={projectId} />
              )}
              {activePanel === "memory" && (
                <MemoryPanel projectId={projectId} />
              )}
            </div>
          </div>
        </div>

        {/* Bottom Panel */}
        <div className="h-64 bg-github-surface border-t border-github-border flex flex-col shrink-0">
          {/* Bottom Panel Tabs */}
          <div className="flex items-center justify-between border-b border-github-border px-4 shrink-0">
            <div className="flex">
              {bottomPanelTabs.map((tab) => (
                <button
                  key={tab.id}
                  className={cn(
                    "px-4 py-2 text-sm font-medium transition-colors",
                    activeBottomPanel === tab.id
                      ? "text-github-primary bg-github-primary/10 border-b-2 border-github-primary"
                      : "text-github-text-secondary hover:text-github-text"
                  )}
                  onClick={() => setActiveBottomPanel(tab.id)}
                >
                  {tab.label}
                </button>
              ))}
            </div>
            <div className="flex items-center space-x-2">
              <button className="text-github-text-secondary hover:text-github-text transition-colors">
                <i className="fas fa-plus text-xs"></i>
              </button>
              <button className="text-github-text-secondary hover:text-github-text transition-colors">
                <i className="fas fa-times text-xs"></i>
              </button>
            </div>
          </div>

          {/* Bottom Panel Content */}
          <div className="flex-1 overflow-y-auto">
            {activeBottomPanel === "terminal" && (
              <Terminal projectId={projectId} />
            )}
            {activeBottomPanel === "git" && (
              <GitPanel
                projectId={projectId}
                gitStatus={gitStatus}
                onCommit={commitChanges}
                onPull={pullChanges}
                onPush={pushChanges}
              />
            )}
          </div>
        </div>
      </div>
      </div>

      {/* Status Bar */}
      <StatusBar
        projectId={projectId}
        gitStatus={gitStatus}
        isConnected={isConnected}
      />
    </div>
  );
}

client/src/pages/memory.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import SpaceBackground from "@/components/SpaceBackground";
import Navigation from "@/components/Navigation";
import { Search, Brain, Plus, Tag, Link as LinkIcon, Calendar, Zap } from "lucide-react";
import { format } from "date-fns";

interface MemoryNote {
  id: string;
  content: string;
  tags: string[];
  links: string[];
  session_id: string;
  created_at: string;
  updated_at: string;
  relevance_score?: number;
}

interface MemoryStats {
  total_notes: number;
  hot_memory_count: number;
  warm_memory_count: number;
  cold_memory_count: number;
  most_used_tags: string[];
}

export default function Memory() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedTags, setSelectedTags] = useState<string[]>([]);
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [newNote, setNewNote] = useState({ content: "", tags: "", links: "" });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch memory notes
  const { data: memoryData, isLoading } = useQuery({
    queryKey: ['/api/memory', searchQuery, selectedTags],
    queryFn: async () => {
      let url = '/api/memory/search';
      const params = new URLSearchParams();

      if (searchQuery.trim()) {
        params.set('query', searchQuery.trim());
      }
      if (selectedTags.length > 0) {
        params.set('tags', selectedTags.join(','));
      }

      if (params.toString()) {
        url += `?${params.toString()}`;
      }

      const response = await apiRequest('GET', url);
      return response.json();
    }
  });

  // Fetch memory stats
  const { data: stats } = useQuery({
    queryKey: ['/api/memory/stats'],
    queryFn: async () => {
      const response = await apiRequest('GET', '/api/memory/stats');
      return response.json();
    }
  });

  // Create memory note
  const createNoteMutation = useMutation({
    mutationFn: async (data: { content: string; tags: string[]; links: string[] }) => {
      const response = await apiRequest('POST', '/api/memory/notes', data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Memory note created",
        description: "Your note has been added to the knowledge base"
      });
      queryClient.invalidateQueries({ queryKey: ['/api/memory'] });
      setIsCreateDialogOpen(false);
      setNewNote({ content: "", tags: "", links: "" });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to create note",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  const handleCreateNote = () => {
    if (!newNote.content.trim()) return;

    const tags = newNote.tags.split(',').map(t => t.trim()).filter(Boolean);
    const links = newNote.links.split(',').map(l => l.trim()).filter(Boolean);

    createNoteMutation.mutate({
      content: newNote.content,
      tags,
      links
    });
  };

  const handleTagClick = (tag: string) => {
    if (selectedTags.includes(tag)) {
      setSelectedTags(selectedTags.filter(t => t !== tag));
    } else {
      setSelectedTags([...selectedTags, tag]);
    }
  };

  // Debounced search
  useEffect(() => {
    const timer = setTimeout(() => {
      queryClient.invalidateQueries({ queryKey: ['/api/memory'] });
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery, queryClient]);

  const getMemoryTier = (score?: number): { tier: string; color: string } => {
    if (!score) return { tier: 'Cold', color: 'text-blue-400' };
    if (score > 0.8) return { tier: 'Hot', color: 'text-red-400' };
    if (score > 0.6) return { tier: 'Warm', color: 'text-yellow-400' };
    return { tier: 'Cold', color: 'text-blue-400' };
  };

  return (
    <div className="min-h-screen relative">
      <SpaceBackground />
      <Navigation />

      <div className="ml-64 relative z-10">
        <div className="p-8">
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-4xl font-bold text-white mb-2">AI Memory System</h1>
            <p className="text-gray-400 text-lg">
              Your intelligent knowledge base that learns and remembers across sessions
            </p>
          </div>

          {/* Stats Cards */}
          {stats && (
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader className="pb-3">
                  <div className="flex items-center gap-3">
                    <Brain className="w-5 h-5 text-blue-400" />
                    <div>
                      <CardTitle className="text-white text-lg">{stats.total_notes}</CardTitle>
                      <CardDescription className="text-gray-400 text-sm">Total Notes</CardDescription>
                    </div>
                  </div>
                </CardHeader>
              </Card>

              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader className="pb-3">
                  <div className="flex items-center gap-3">
                    <Zap className="w-5 h-5 text-red-400" />
                    <div>
                      <CardTitle className="text-white text-lg">{stats.hot_memory_count}</CardTitle>
                      <CardDescription className="text-gray-400 text-sm">Hot Memory</CardDescription>
                    </div>
                  </div>
                </CardHeader>
              </Card>

              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader className="pb-3">
                  <div className="flex items-center gap-3">
                    <div className="w-5 h-5 bg-yellow-400 rounded-full"></div>
                    <div>
                      <CardTitle className="text-white text-lg">{stats.warm_memory_count}</CardTitle>
                      <CardDescription className="text-gray-400 text-sm">Warm Memory</CardDescription>
                    </div>
                  </div>
                </CardHeader>
              </Card>

              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader className="pb-3">
                  <div className="flex items-center gap-3">
                    <div className="w-5 h-5 bg-blue-400 rounded-full"></div>
                    <div>
                      <CardTitle className="text-white text-lg">{stats.cold_memory_count}</CardTitle>
                      <CardDescription className="text-gray-400 text-sm">Cold Memory</CardDescription>
                    </div>
                  </div>
                </CardHeader>
              </Card>
            </div>
          )}

          {/* Search and Controls */}
          <div className="bg-black/20 backdrop-blur-xl rounded-2xl border border-white/10 p-6 mb-8">
            <div className="flex flex-col lg:flex-row gap-4">
              {/* Search */}
              <div className="flex-1 relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                <Input
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Search your memory by content, tags, or concepts..."
                  className="pl-10 bg-white/5 border-white/20 text-white placeholder-gray-400 focus:border-blue-500"
                />
              </div>

              <Button
                onClick={() => setIsCreateDialogOpen(true)}
                className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Memory Note
              </Button>
            </div>

            {/* Selected Tags */}
            {selectedTags.length > 0 && (
              <div className="flex flex-wrap gap-2 mt-4">
                <span className="text-sm text-gray-400">Filtered by:</span>
                {selectedTags.map((tag) => (
                  <Badge
                    key={tag}
                    className="bg-blue-500/20 text-blue-300 border-blue-500/30 cursor-pointer"
                    onClick={() => handleTagClick(tag)}
                  >
                    {tag} Ã—
                  </Badge>
                ))}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setSelectedTags([])}
                  className="text-gray-400 hover:text-white"
                >
                  Clear all
                </Button>
              </div>
            )}
          </div>

          {/* Popular Tags */}
          {stats?.most_used_tags?.length > 0 && (
            <div className="mb-8">
              <h3 className="text-lg font-semibold text-white mb-4">Popular Tags</h3>
              <div className="flex flex-wrap gap-2">
                {stats.most_used_tags.map((tag) => (
                  <Badge
                    key={tag}
                    variant="outline"
                    className={`cursor-pointer transition-colors ${
                      selectedTags.includes(tag)
                        ? "bg-blue-500/20 text-blue-300 border-blue-500/30"
                        : "border-white/20 text-gray-400 hover:border-white/40"
                    }`}
                    onClick={() => handleTagClick(tag)}
                  >
                    <Tag className="w-3 h-3 mr-1" />
                    {tag}
                  </Badge>
                ))}
              </div>
            </div>
          )}

          {/* Memory Notes */}
          {isLoading ? (
            <div className="space-y-4">
              {Array.from({ length: 5 }).map((_, i) => (
                <Card key={i} className="bg-white/5 border-white/10 animate-pulse">
                  <CardHeader>
                    <div className="w-full h-20 bg-white/10 rounded mb-4"></div>
                    <div className="flex gap-2">
                      <div className="w-16 h-6 bg-white/10 rounded"></div>
                      <div className="w-20 h-6 bg-white/10 rounded"></div>
                    </div>
                  </CardHeader>
                </Card>
              ))}
            </div>
          ) : memoryData?.notes?.length ? (
            <div className="space-y-4">
              {memoryData.notes.map((note: MemoryNote) => {
                const memoryTier = getMemoryTier(note.relevance_score);
                return (
                  <Card
                    key={note.id}
                    className="bg-black/40 backdrop-blur-xl border-white/10 hover:border-blue-500/30 transition-all duration-200"
                  >
                    <CardHeader>
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-2">
                            <Badge className={`${memoryTier.color} bg-transparent border-current`}>
                              {memoryTier.tier} Memory
                            </Badge>
                            {note.relevance_score && (
                              <span className="text-xs text-gray-500">
                                {Math.round(note.relevance_score * 100)}% relevance
                              </span>
                            )}
                          </div>
                          <CardDescription className="text-white text-base leading-relaxed">
                            {note.content}
                          </CardDescription>
                        </div>
                      </div>

                      {/* Tags and Links */}
                      <div className="flex flex-wrap gap-2 mt-4">
                        {note.tags.map((tag) => (
                          <Badge
                            key={tag}
                            variant="outline"
                            className="text-xs border-white/20 text-gray-400 cursor-pointer hover:border-blue-500/50"
                            onClick={() => handleTagClick(tag)}
                          >
                            <Tag className="w-3 h-3 mr-1" />
                            {tag}
                          </Badge>
                        ))}

                        {note.links.map((link) => (
                          <Badge
                            key={link}
                            variant="outline"
                            className="text-xs border-green-500/20 text-green-400"
                          >
                            <LinkIcon className="w-3 h-3 mr-1" />
                            {link}
                          </Badge>
                        ))}
                      </div>

                      {/* Metadata */}
                      <div className="flex items-center justify-between mt-4 pt-4 border-t border-white/10">
                        <div className="flex items-center gap-4 text-xs text-gray-500">
                          <div className="flex items-center gap-1">
                            <Calendar className="w-3 h-3" />
                            {format(new Date(note.created_at), 'MMM d, yyyy')}
                          </div>
                          <div>Session: {note.session_id.slice(0, 8)}</div>
                        </div>
                      </div>
                    </CardHeader>
                  </Card>
                );
              })}
            </div>
          ) : (
            <div className="text-center py-16">
              <div className="w-20 h-20 bg-gradient-to-br from-blue-500/20 to-purple-500/20 rounded-2xl flex items-center justify-center mx-auto mb-6 border border-blue-500/20">
                <Brain className="w-10 h-10 text-blue-400" />
              </div>
              <h3 className="text-2xl font-semibold mb-3 text-white">No memory notes found</h3>
              <p className="text-gray-400 mb-8 max-w-md mx-auto leading-relaxed">
                {searchQuery
                  ? `No memories match "${searchQuery}". Try different search terms.`
                  : "Start adding memory notes to build your AI knowledge base."}
              </p>
              <Button
                onClick={() => setIsCreateDialogOpen(true)}
                className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white"
              >
                <Plus className="w-4 h-4 mr-2" />
                Create Your First Memory Note
              </Button>
            </div>
          )}

          {/* Create Memory Note Dialog */}
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogContent className="bg-black/90 backdrop-blur-xl border-white/20 text-white max-w-2xl">
              <DialogHeader>
                <DialogTitle className="text-2xl">Add Memory Note</DialogTitle>
                <DialogDescription className="text-gray-400">
                  Create a new memory note that the AI can learn from and reference
                </DialogDescription>
              </DialogHeader>

              <div className="space-y-6">
                <div>
                  <label className="block text-sm font-medium mb-3 text-white">
                    Content *
                  </label>
                  <Textarea
                    value={newNote.content}
                    onChange={(e) => setNewNote(prev => ({ ...prev, content: e.target.value }))}
                    placeholder="Describe what you learned, decisions made, code patterns, or important insights..."
                    className="bg-white/5 border-white/20 text-white placeholder-gray-400 focus:border-blue-500 min-h-32"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-3 text-white">
                    Tags (comma-separated)
                  </label>
                  <Input
                    value={newNote.tags}
                    onChange={(e) => setNewNote(prev => ({ ...prev, tags: e.target.value }))}
                    placeholder="react, database, bug-fix, architecture..."
                    className="bg-white/5 border-white/20 text-white placeholder-gray-400 focus:border-blue-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-3 text-white">
                    Related Links (comma-separated)
                  </label>
                  <Input
                    value={newNote.links}
                    onChange={(e) => setNewNote(prev => ({ ...prev, links: e.target.value }))}
                    placeholder="project-name, file.js, function-name..."
                    className="bg-white/5 border-white/20 text-white placeholder-gray-400 focus:border-blue-500"
                  />
                </div>
              </div>

              <DialogFooter>
                <Button
                  variant="outline"
                  onClick={() => setIsCreateDialogOpen(false)}
                  className="border-white/20 text-white hover:bg-white/10"
                >
                  Cancel
                </Button>
                <Button
                  onClick={handleCreateNote}
                  disabled={createNoteMutation.isPending || !newNote.content.trim()}
                  className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white"
                >
                  {createNoteMutation.isPending ? "Creating..." : "Create Note"}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </div>
      </div>
    </div>
  );
}

