client/src/pages/projects.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useLocation } from "wouter";
import SpaceBackground from "@/components/SpaceBackground";
import Navigation from "@/components/Navigation";
import { 
  Search, 
  Plus, 
  Folder, 
  Calendar, 
  Code2, 
  GitBranch, 
  Clock, 
  MoreVertical, 
  Trash2, 
  Edit3,
  ExternalLink,
  Star,
  StarOff
} from "lucide-react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { formatDistanceToNow } from "date-fns";

interface Project {
  id: string;
  name: string;
  description: string;
  language: string;
  framework?: string;
  status: 'active' | 'archived' | 'template';
  isStarred: boolean;
  lastAccessed: string;
  createdAt: string;
  updatedAt: string;
  gitBranch?: string;
  collaborators?: number;
}

const PROJECT_STATUSES = [
  { id: 'all', name: 'All Projects', count: 0 },
  { id: 'active', name: 'Active', count: 0 },
  { id: 'archived', name: 'Archived', count: 0 },
  { id: 'template', name: 'Templates', count: 0 },
];

export default function Projects() {
  const [, setLocation] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedStatus, setSelectedStatus] = useState("all");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch projects
  const { data: projects = [], isLoading } = useQuery({
    queryKey: ['/api/projects', selectedStatus, searchQuery],
    queryFn: async () => {
      let url = '/api/projects';
      const params = new URLSearchParams();

      if (selectedStatus !== 'all') {
        params.set('status', selectedStatus);
      }
      if (searchQuery.trim()) {
        params.set('search', searchQuery.trim());
      }

      if (params.toString()) {
        url += `?${params.toString()}`;
      }

      const response = await apiRequest('GET', url);
      return response.json();
    }
  });

  // Create new project
  const createProjectMutation = useMutation({
    mutationFn: async (projectData: { name: string; description: string; language: string; framework?: string }) => {
      const response = await apiRequest('POST', '/api/projects', {
        body: JSON.stringify(projectData)
      });
      return response.json();
    },
    onSuccess: (project) => {
      toast({
        title: "Project created successfully",
        description: `${project.name} is ready to use`
      });
      queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
      setLocation(`/ide/${project.id}`);
    },
    onError: (error: any) => {
      toast({
        title: "Failed to create project",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  // Delete project
  const deleteProjectMutation = useMutation({
    mutationFn: async (projectId: string) => {
      await apiRequest('DELETE', `/api/projects/${projectId}`);
    },
    onSuccess: () => {
      toast({
        title: "Project deleted",
        description: "Project has been permanently deleted"
      });
      queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to delete project",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  // Toggle star project
  const toggleStarMutation = useMutation({
    mutationFn: async ({ projectId, isStarred }: { projectId: string; isStarred: boolean }) => {
      await apiRequest('PATCH', `/api/projects/${projectId}`, {
        body: JSON.stringify({ isStarred: !isStarred })
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/projects'] });
    }
  });

  const handleCreateProject = () => {
    // For now, create a simple React TypeScript project
    createProjectMutation.mutate({
      name: `Project ${projects.length + 1}`,
      description: "A new project created from scratch",
      language: "typescript",
      framework: "react"
    });
  };

  const handleDeleteProject = (projectId: string, projectName: string) => {
    if (window.confirm(`Are you sure you want to delete "${projectName}"? This action cannot be undone.`)) {
      deleteProjectMutation.mutate(projectId);
    }
  };

  const handleToggleStar = (project: Project) => {
    toggleStarMutation.mutate({ projectId: project.id, isStarred: project.isStarred });
  };

  const getLanguageColor = (language: string) => {
    const colors: Record<string, string> = {
      typescript: "bg-blue-500/20 text-blue-300 border-blue-500/30",
      javascript: "bg-yellow-500/20 text-yellow-300 border-yellow-500/30",
      python: "bg-green-500/20 text-green-300 border-green-500/30",
      java: "bg-red-500/20 text-red-300 border-red-500/30",
      go: "bg-cyan-500/20 text-cyan-300 border-cyan-500/30",
      rust: "bg-orange-500/20 text-orange-300 border-orange-500/30"
    };
    return colors[language.toLowerCase()] || "bg-gray-500/20 text-gray-300 border-gray-500/30";
  };

  const filteredProjects = projects.filter((project: Project) => {
    const matchesStatus = selectedStatus === 'all' || project.status === selectedStatus;
    const matchesSearch = !searchQuery.trim() || 
      project.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      project.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
      project.language.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesStatus && matchesSearch;
  });

  return (
    <div className="min-h-screen relative">
      <SpaceBackground />
      <Navigation />

      <div className="ml-64 relative z-10">
        <div className="p-8">
          {/* Header */}
          <div className="flex items-center justify-between mb-8">
            <div>
              <h1 className="text-4xl font-bold text-white mb-2">My Projects</h1>
              <p className="text-gray-400 text-lg">
                Manage and organize your development projects
              </p>
            </div>
            <Button
              onClick={handleCreateProject}
              disabled={createProjectMutation.isPending}
              className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white"
            >
              <Plus className="w-4 h-4 mr-2" />
              {createProjectMutation.isPending ? "Creating..." : "New Project"}
            </Button>
          </div>

          {/* Search and Filters */}
          <div className="bg-black/20 backdrop-blur-xl rounded-2xl border border-white/10 p-6 mb-8">
            <div className="flex flex-col lg:flex-row gap-4">
              {/* Search */}
              <div className="flex-1 relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                <Input
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Search projects by name, language, or description..."
                  className="pl-10 bg-white/5 border-white/20 text-white placeholder-gray-400 focus:border-blue-500"
                />
              </div>

              {/* Status Filter */}
              <div className="flex flex-wrap gap-2">
                {PROJECT_STATUSES.map((status) => (
                  <Button
                    key={status.id}
                    variant={selectedStatus === status.id ? "default" : "outline"}
                    size="sm"
                    onClick={() => setSelectedStatus(status.id)}
                    className={
                      selectedStatus === status.id
                        ? "bg-gradient-to-r from-blue-600 to-purple-600 text-white border-0"
                        : "border-white/20 text-white hover:bg-white/10 bg-transparent"
                    }
                  >
                    {status.name}
                  </Button>
                ))}
              </div>
            </div>
          </div>

          {/* Projects Grid */}
          {isLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {Array.from({ length: 6 }).map((_, i) => (
                <Card key={i} className="bg-white/5 border-white/10 animate-pulse">
                  <CardHeader>
                    <div className="w-full h-4 bg-white/10 rounded mb-2"></div>
                    <div className="w-2/3 h-3 bg-white/10 rounded"></div>
                  </CardHeader>
                  <CardContent>
                    <div className="w-full h-12 bg-white/10 rounded mb-4"></div>
                    <div className="flex gap-2">
                      <div className="w-16 h-6 bg-white/10 rounded"></div>
                      <div className="w-20 h-6 bg-white/10 rounded"></div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : filteredProjects.length ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredProjects.map((project: Project) => (
                <Card
                  key={project.id}
                  className="bg-black/40 backdrop-blur-xl border-white/10 hover:border-blue-500/50 transition-all duration-300 group hover:shadow-2xl hover:shadow-blue-500/10 cursor-pointer"
                  onClick={() => setLocation(`/ide/${project.id}`)}
                >
                  <CardHeader>
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <CardTitle className="text-white text-lg group-hover:text-blue-300 transition-colors">
                            {project.name}
                          </CardTitle>
                          <button
                            onClick={(e) => {
                              e.stopPropagation();
                              handleToggleStar(project);
                            }}
                            className="text-gray-400 hover:text-yellow-400 transition-colors"
                          >
                            {project.isStarred ? (
                              <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
                            ) : (
                              <StarOff className="w-4 h-4" />
                            )}
                          </button>
                        </div>
                        <CardDescription className="text-gray-400 text-sm line-clamp-2">
                          {project.description}
                        </CardDescription>
                      </div>

                      <DropdownMenu>
                        <DropdownMenuTrigger asChild onClick={(e) => e.stopPropagation()}>
                          <Button variant="ghost" size="sm" className="text-gray-400 hover:text-white">
                            <MoreVertical className="w-4 h-4" />
                          </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent className="bg-black/80 backdrop-blur-xl border-white/10">
                          <DropdownMenuItem onClick={(e) => {
                            e.stopPropagation();
                            setLocation(`/ide/${project.id}`);
                          }}>
                            <ExternalLink className="w-4 h-4 mr-2" />
                            Open in IDE
                          </DropdownMenuItem>
                          <DropdownMenuItem onClick={(e) => e.stopPropagation()}>
                            <Edit3 className="w-4 h-4 mr-2" />
                            Rename
                          </DropdownMenuItem>
                          <DropdownMenuSeparator className="bg-white/10" />
                          <DropdownMenuItem 
                            onClick={(e) => {
                              e.stopPropagation();
                              handleDeleteProject(project.id, project.name);
                            }}
                            className="text-red-400 focus:text-red-300"
                          >
                            <Trash2 className="w-4 h-4 mr-2" />
                            Delete
                          </DropdownMenuItem>
                        </DropdownMenuContent>
                      </DropdownMenu>
                    </div>

                    {/* Project Stats */}
                    <div className="flex items-center gap-4 mt-3 text-xs text-gray-500">
                      <div className="flex items-center gap-1">
                        <Clock className="w-3 h-3" />
                        {formatDistanceToNow(new Date(project.lastAccessed), { addSuffix: true })}
                      </div>
                      {project.gitBranch && (
                        <div className="flex items-center gap-1">
                          <GitBranch className="w-3 h-3" />
                          {project.gitBranch}
                        </div>
                      )}
                      {project.collaborators && project.collaborators > 1 && (
                        <div className="flex items-center gap-1">
                          <span>{project.collaborators} collaborators</span>
                        </div>
                      )}
                    </div>
                  </CardHeader>

                  <CardContent>
                    {/* Tags */}
                    <div className="flex flex-wrap gap-2 mb-4">
                      <Badge className={`text-xs ${getLanguageColor(project.language)}`}>
                        {project.language}
                      </Badge>
                      {project.framework && (
                        <Badge variant="outline" className="text-xs border-white/20 text-gray-400">
                          {project.framework}
                        </Badge>
                      )}
                      <Badge variant="outline" className="text-xs border-white/20 text-gray-400">
                        {project.status}
                      </Badge>
                    </div>

                    <div className="text-xs text-gray-500">
                      Created {formatDistanceToNow(new Date(project.createdAt), { addSuffix: true })}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            <div className="text-center py-16">
              <div className="w-20 h-20 bg-gradient-to-br from-blue-500/20 to-purple-500/20 rounded-2xl flex items-center justify-center mx-auto mb-6 border border-blue-500/20">
                <Folder className="w-10 h-10 text-blue-400" />
              </div>
              <h3 className="text-2xl font-semibold mb-3 text-white">
                {searchQuery ? "No projects found" : "No projects yet"}
              </h3>
              <p className="text-gray-400 mb-8 max-w-md mx-auto leading-relaxed">
                {searchQuery
                  ? `No projects match "${searchQuery}". Try adjusting your search terms.`
                  : "Create your first project to get started with development."}
              </p>
              <div className="flex gap-4 justify-center">
                {searchQuery && (
                  <Button
                    onClick={() => {
                      setSearchQuery("");
                      setSelectedStatus("all");
                    }}
                    variant="outline"
                    className="border-white/20 text-white hover:bg-white/10"
                  >
                    Clear Search
                  </Button>
                )}
                <Button
                  onClick={handleCreateProject}
                  disabled={createProjectMutation.isPending}
                  className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  {createProjectMutation.isPending ? "Creating..." : "Create New Project"}
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/recent.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useLocation } from "wouter";
import SpaceBackground from "@/components/SpaceBackground";
import Navigation from "@/components/Navigation";
import { 
  Search, 
  Clock, 
  Code2, 
  GitBranch, 
  Calendar,
  ExternalLink,
  Star,
  Eye
} from "lucide-react";
import { formatDistanceToNow, format } from "date-fns";

interface RecentActivity {
  id: string;
  projectId: string;
  projectName: string;
  type: 'opened' | 'edited' | 'created' | 'committed' | 'deployed';
  description: string;
  timestamp: string;
  language: string;
  framework?: string;
  filePath?: string;
  gitBranch?: string;
}

const ACTIVITY_TYPES = [
  { id: 'all', name: 'All Activity' },
  { id: 'opened', name: 'Opened' },
  { id: 'edited', name: 'Edited' },
  { id: 'created', name: 'Created' },
  { id: 'committed', name: 'Committed' },
  { id: 'deployed', name: 'Deployed' },
];

const TIME_FILTERS = [
  { id: 'all', name: 'All Time' },
  { id: 'today', name: 'Today' },
  { id: 'week', name: 'This Week' },
  { id: 'month', name: 'This Month' },
];

export default function Recent() {
  const [, setLocation] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedType, setSelectedType] = useState("all");
  const [selectedTimeFilter, setSelectedTimeFilter] = useState("all");

  // Fetch recent activities
  const { data: activities = [], isLoading } = useQuery({
    queryKey: ['/api/activities/recent', selectedType, selectedTimeFilter, searchQuery],
    queryFn: async () => {
      let url = '/api/activities/recent';
      const params = new URLSearchParams();

      if (selectedType !== 'all') {
        params.set('type', selectedType);
      }
      if (selectedTimeFilter !== 'all') {
        params.set('timeFilter', selectedTimeFilter);
      }
      if (searchQuery.trim()) {
        params.set('search', searchQuery.trim());
      }

      if (params.toString()) {
        url += `?${params.toString()}`;
      }

      // Mock data for now - in production this would fetch from API
      return [
        {
          id: '1',
          projectId: 'proj1',
          projectName: 'React Dashboard',
          type: 'edited',
          description: 'Modified components/Dashboard.tsx',
          timestamp: new Date(Date.now() - 1000 * 60 * 15).toISOString(), // 15 mins ago
          language: 'typescript',
          framework: 'react',
          filePath: 'src/components/Dashboard.tsx',
          gitBranch: 'feature/new-charts'
        },
        {
          id: '2',
          projectId: 'proj2',
          projectName: 'API Server',
          type: 'committed',
          description: 'Added user authentication endpoints',
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 2).toISOString(), // 2 hours ago
          language: 'typescript',
          framework: 'express',
          gitBranch: 'main'
        },
        {
          id: '3',
          projectId: 'proj3',
          projectName: 'Mobile App',
          type: 'opened',
          description: 'Opened project in IDE',
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 4).toISOString(), // 4 hours ago
          language: 'javascript',
          framework: 'react-native'
        },
        {
          id: '4',
          projectId: 'proj1',
          projectName: 'React Dashboard',
          type: 'deployed',
          description: 'Deployed to production',
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 24).toISOString(), // 1 day ago
          language: 'typescript',
          framework: 'react'
        },
        {
          id: '5',
          projectId: 'proj4',
          projectName: 'AI Chat Bot',
          type: 'created',
          description: 'Created new project from template',
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 24 * 2).toISOString(), // 2 days ago
          language: 'python',
          framework: 'fastapi'
        }
      ].filter(activity => {
        const matchesType = selectedType === 'all' || activity.type === selectedType;
        const matchesSearch = !searchQuery.trim() || 
          activity.projectName.toLowerCase().includes(searchQuery.toLowerCase()) ||
          activity.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
          (activity.filePath && activity.filePath.toLowerCase().includes(searchQuery.toLowerCase()));

        let matchesTime = true;
        if (selectedTimeFilter !== 'all') {
          const activityTime = new Date(activity.timestamp);
          const now = new Date();

          switch (selectedTimeFilter) {
            case 'today':
              matchesTime = activityTime.toDateString() === now.toDateString();
              break;
            case 'week':
              const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
              matchesTime = activityTime >= weekAgo;
              break;
            case 'month':
              const monthAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
              matchesTime = activityTime >= monthAgo;
              break;
          }
        }

        return matchesType && matchesSearch && matchesTime;
      });
    }
  });

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'opened':
        return <ExternalLink className="w-4 h-4 text-blue-400" />;
      case 'edited':
        return <Code2 className="w-4 h-4 text-green-400" />;
      case 'created':
        return <Star className="w-4 h-4 text-purple-400" />;
      case 'committed':
        return <GitBranch className="w-4 h-4 text-orange-400" />;
      case 'deployed':
        return <Eye className="w-4 h-4 text-pink-400" />;
      default:
        return <Clock className="w-4 h-4 text-gray-400" />;
    }
  };

  const getActivityColor = (type: string) => {
    switch (type) {
      case 'opened':
        return 'text-blue-400';
      case 'edited':
        return 'text-green-400';
      case 'created':
        return 'text-purple-400';
      case 'committed':
        return 'text-orange-400';
      case 'deployed':
        return 'text-pink-400';
      default:
        return 'text-gray-400';
    }
  };

  const getLanguageColor = (language: string) => {
    const colors: Record<string, string> = {
      typescript: "bg-blue-500/20 text-blue-300 border-blue-500/30",
      javascript: "bg-yellow-500/20 text-yellow-300 border-yellow-500/30",
      python: "bg-green-500/20 text-green-300 border-green-500/30",
      java: "bg-red-500/20 text-red-300 border-red-500/30",
      go: "bg-cyan-500/20 text-cyan-300 border-cyan-500/30",
      rust: "bg-orange-500/20 text-orange-300 border-orange-500/30"
    };
    return colors[language.toLowerCase()] || "bg-gray-500/20 text-gray-300 border-gray-500/30";
  };

  return (
    <div className="min-h-screen relative">
      <SpaceBackground />
      <Navigation />

      <div className="ml-64 relative z-10">
        <div className="p-8">
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-4xl font-bold text-white mb-2">Recent Activity</h1>
            <p className="text-gray-400 text-lg">
              Track your recent development activities and project updates
            </p>
          </div>

          {/* Search and Filters */}
          <div className="bg-black/20 backdrop-blur-xl rounded-2xl border border-white/10 p-6 mb-8">
            <div className="flex flex-col lg:flex-row gap-4 mb-4">
              {/* Search */}
              <div className="flex-1 relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                <Input
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Search activities by project, file, or description..."
                  className="pl-10 bg-white/5 border-white/20 text-white placeholder-gray-400 focus:border-blue-500"
                />
              </div>
            </div>

            <div className="flex flex-col lg:flex-row gap-4">
              {/* Activity Type Filter */}
              <div className="flex flex-wrap gap-2">
                {ACTIVITY_TYPES.map((type) => (
                  <Button
                    key={type.id}
                    variant={selectedType === type.id ? "default" : "outline"}
                    size="sm"
                    onClick={() => setSelectedType(type.id)}
                    className={
                      selectedType === type.id
                        ? "bg-gradient-to-r from-blue-600 to-purple-600 text-white border-0"
                        : "border-white/20 text-white hover:bg-white/10 bg-transparent"
                    }
                  >
                    {type.name}
                  </Button>
                ))}
              </div>

              {/* Time Filter */}
              <div className="flex flex-wrap gap-2">
                {TIME_FILTERS.map((filter) => (
                  <Button
                    key={filter.id}
                    variant={selectedTimeFilter === filter.id ? "default" : "outline"}
                    size="sm"
                    onClick={() => setSelectedTimeFilter(filter.id)}
                    className={
                      selectedTimeFilter === filter.id
                        ? "bg-gradient-to-r from-purple-600 to-pink-600 text-white border-0"
                        : "border-white/20 text-white hover:bg-white/10 bg-transparent"
                    }
                  >
                    <Calendar className="w-3 h-3 mr-1" />
                    {filter.name}
                  </Button>
                ))}
              </div>
            </div>
          </div>

          {/* Activity List */}
          {isLoading ? (
            <div className="space-y-4">
              {Array.from({ length: 8 }).map((_, i) => (
                <Card key={i} className="bg-white/5 border-white/10 animate-pulse">
                  <CardContent className="p-4">
                    <div className="flex gap-4">
                      <div className="w-10 h-10 bg-white/10 rounded-full"></div>
                      <div className="flex-1">
                        <div className="w-1/3 h-4 bg-white/10 rounded mb-2"></div>
                        <div className="w-2/3 h-3 bg-white/10 rounded mb-2"></div>
                        <div className="flex gap-2">
                          <div className="w-16 h-5 bg-white/10 rounded"></div>
                          <div className="w-20 h-5 bg-white/10 rounded"></div>
                        </div>
                      </div>
                      <div className="w-20 h-4 bg-white/10 rounded"></div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : activities.length ? (
            <div className="space-y-4">
              {activities.map((activity) => (
                <Card
                  key={activity.id}
                  className="bg-black/40 backdrop-blur-xl border-white/10 hover:border-blue-500/50 transition-all duration-300 group hover:shadow-lg hover:shadow-blue-500/10 cursor-pointer"
                  onClick={() => setLocation(`/ide/${activity.projectId}`)}
                >
                  <CardContent className="p-6">
                    <div className="flex items-start gap-4">
                      {/* Activity Icon */}
                      <div className="w-10 h-10 bg-black/40 border border-white/10 rounded-full flex items-center justify-center flex-shrink-0">
                        {getActivityIcon(activity.type)}
                      </div>

                      {/* Activity Details */}
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2 mb-2">
                          <h3 className="text-white font-medium text-lg group-hover:text-blue-300 transition-colors">
                            {activity.projectName}
                          </h3>
                          <span className={`text-sm font-medium capitalize ${getActivityColor(activity.type)}`}>
                            {activity.type}
                          </span>
                        </div>

                        <p className="text-gray-400 text-sm mb-3 leading-relaxed">
                          {activity.description}
                        </p>

                        {activity.filePath && (
                          <p className="text-gray-500 text-xs mb-3 font-mono bg-white/5 px-2 py-1 rounded inline-block">
                            {activity.filePath}
                          </p>
                        )}

                        {/* Tags */}
                        <div className="flex flex-wrap gap-2">
                          <Badge className={`text-xs ${getLanguageColor(activity.language)}`}>
                            {activity.language}
                          </Badge>
                          {activity.framework && (
                            <Badge variant="outline" className="text-xs border-white/20 text-gray-400">
                              {activity.framework}
                            </Badge>
                          )}
                          {activity.gitBranch && (
                            <Badge variant="outline" className="text-xs border-white/20 text-gray-400">
                              <GitBranch className="w-3 h-3 mr-1" />
                              {activity.gitBranch}
                            </Badge>
                          )}
                        </div>
                      </div>

                      {/* Timestamp */}
                      <div className="text-right text-xs text-gray-500 flex-shrink-0">
                        <div className="mb-1">
                          {formatDistanceToNow(new Date(activity.timestamp), { addSuffix: true })}
                        </div>
                        <div>
                          {format(new Date(activity.timestamp), 'MMM d, HH:mm')}
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            <div className="text-center py-16">
              <div className="w-20 h-20 bg-gradient-to-br from-blue-500/20 to-purple-500/20 rounded-2xl flex items-center justify-center mx-auto mb-6 border border-blue-500/20">
                <Clock className="w-10 h-10 text-blue-400" />
              </div>
              <h3 className="text-2xl font-semibold mb-3 text-white">No recent activity</h3>
              <p className="text-gray-400 mb-8 max-w-md mx-auto leading-relaxed">
                {searchQuery
                  ? `No activities match "${searchQuery}". Try adjusting your search terms or filters.`
                  : "Start working on projects to see your recent development activities here."}
              </p>
              {searchQuery && (
                <Button
                  onClick={() => {
                    setSearchQuery("");
                    setSelectedType("all");
                    setSelectedTimeFilter("all");
                  }}
                  variant="outline"
                  className="border-white/20 text-white hover:bg-white/10"
                >
                  Clear Filters
                </Button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/settings.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import SpaceBackground from "@/components/SpaceBackground";
import Navigation from "@/components/Navigation";
import { 
  Settings as SettingsIcon, 
  User, 
  Palette, 
  Code, 
  Shield, 
  Bell, 
  Globe, 
  Zap, 
  Database,
  Key,
  Save,
  RefreshCw,
  Trash2,
  Download,
  Upload,
  Eye,
  EyeOff,
  Monitor,
  Moon,
  Sun
} from "lucide-react";

interface UserSettings {
  // Profile
  name: string;
  email: string;
  bio: string;
  avatar?: string;

  // Appearance
  theme: 'light' | 'dark' | 'auto';
  fontSize: number;
  fontFamily: string;
  lineHeight: number;

  // Editor
  tabSize: number;
  wordWrap: boolean;
  autoSave: boolean;
  showLineNumbers: boolean;
  showMinimap: boolean;
  formatOnSave: boolean;

  // Privacy & Security
  profileVisibility: 'public' | 'private';
  allowCollaboration: boolean;
  twoFactorEnabled: boolean;

  // Notifications
  emailNotifications: boolean;
  pushNotifications: boolean;
  projectUpdates: boolean;
  securityAlerts: boolean;

  // Advanced
  aiAssistance: boolean;
  autoComplete: boolean;
  gitAutoCommit: boolean;
  debugMode: boolean;
}

const FONT_FAMILIES = [
  { value: 'JetBrains Mono', label: 'JetBrains Mono' },
  { value: 'Fira Code', label: 'Fira Code' },
  { value: 'Source Code Pro', label: 'Source Code Pro' },
  { value: 'Inconsolata', label: 'Inconsolata' },
  { value: 'Monaco', label: 'Monaco' },
  { value: 'Consolas', label: 'Consolas' }
];

const FONT_SIZES = [10, 11, 12, 13, 14, 15, 16, 17, 18, 20, 22, 24];
const TAB_SIZES = [2, 4, 8];
const LINE_HEIGHTS = [1.2, 1.4, 1.5, 1.6, 1.8, 2.0];

export default function Settings() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showApiKey, setShowApiKey] = useState(false);
  const [activeTab, setActiveTab] = useState("profile");

  // Fetch user settings
  const { data: settings, isLoading } = useQuery({
    queryKey: ['/api/settings'],
    queryFn: async () => {
      // Mock data for now - in production this would fetch from API
      return {
        name: 'John Developer',
        email: 'john@example.com',
        bio: 'Full-stack developer passionate about AI and modern web technologies',
        theme: 'dark',
        fontSize: 14,
        fontFamily: 'JetBrains Mono',
        lineHeight: 1.5,
        tabSize: 2,
        wordWrap: true,
        autoSave: true,
        showLineNumbers: true,
        showMinimap: true,
        formatOnSave: true,
        profileVisibility: 'public',
        allowCollaboration: true,
        twoFactorEnabled: false,
        emailNotifications: true,
        pushNotifications: true,
        projectUpdates: true,
        securityAlerts: true,
        aiAssistance: true,
        autoComplete: true,
        gitAutoCommit: false,
        debugMode: false
      } as UserSettings;
    }
  });

  // Update settings
  const updateSettingsMutation = useMutation({
    mutationFn: async (newSettings: Partial<UserSettings>) => {
      await apiRequest('PATCH', '/api/settings', {
        body: JSON.stringify(newSettings)
      });
      return newSettings;
    },
    onSuccess: () => {
      toast({
        title: "Settings saved",
        description: "Your preferences have been updated successfully"
      });
      queryClient.invalidateQueries({ queryKey: ['/api/settings'] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to save settings",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  const handleSettingChange = (key: keyof UserSettings, value: any) => {
    updateSettingsMutation.mutate({ [key]: value });
  };

  const handleBulkUpdate = (updates: Partial<UserSettings>) => {
    updateSettingsMutation.mutate(updates);
  };

  if (isLoading || !settings) {
    return (
      <div className="min-h-screen relative">
        <SpaceBackground />
        <Navigation />
        <div className="ml-64 relative z-10 flex items-center justify-center h-screen">
          <RefreshCw className="w-8 h-8 text-blue-400 animate-spin" />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen relative">
      <SpaceBackground />
      <Navigation />

      <div className="ml-64 relative z-10">
        <div className="p-8">
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-4xl font-bold text-white mb-2 flex items-center gap-3">
              <SettingsIcon className="w-8 h-8 text-blue-400" />
              Settings
            </h1>
            <p className="text-gray-400 text-lg">
              Customize your development environment and preferences
            </p>
          </div>

          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="bg-black/20 border border-white/10 p-1">
              <TabsTrigger value="profile" className="data-[state=active]:bg-blue-600/20 data-[state=active]:text-blue-300">
                <User className="w-4 h-4 mr-2" />
                Profile
              </TabsTrigger>
              <TabsTrigger value="appearance" className="data-[state=active]:bg-blue-600/20 data-[state=active]:text-blue-300">
                <Palette className="w-4 h-4 mr-2" />
                Appearance
              </TabsTrigger>
              <TabsTrigger value="editor" className="data-[state=active]:bg-blue-600/20 data-[state=active]:text-blue-300">
                <Code className="w-4 h-4 mr-2" />
                Editor
              </TabsTrigger>
              <TabsTrigger value="security" className="data-[state=active]:bg-blue-600/20 data-[state=active]:text-blue-300">
                <Shield className="w-4 h-4 mr-2" />
                Security
              </TabsTrigger>
              <TabsTrigger value="notifications" className="data-[state=active]:bg-blue-600/20 data-[state=active]:text-blue-300">
                <Bell className="w-4 h-4 mr-2" />
                Notifications
              </TabsTrigger>
              <TabsTrigger value="advanced" className="data-[state=active]:bg-blue-600/20 data-[state=active]:text-blue-300">
                <Zap className="w-4 h-4 mr-2" />
                Advanced
              </TabsTrigger>
            </TabsList>

            {/* Profile Settings */}
            <TabsContent value="profile" className="space-y-6">
              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader>
                  <CardTitle className="text-white flex items-center gap-2">
                    <User className="w-5 h-5 text-blue-400" />
                    Profile Information
                  </CardTitle>
                  <CardDescription className="text-gray-400">
                    Manage your public profile and personal information
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="name" className="text-white">Display Name</Label>
                      <Input
                        id="name"
                        value={settings.name}
                        onChange={(e) => handleSettingChange('name', e.target.value)}
                        className="bg-white/5 border-white/20 text-white"
                      />
                    </div>
                    <div>
                      <Label htmlFor="email" className="text-white">Email</Label>
                      <Input
                        id="email"
                        type="email"
                        value={settings.email}
                        onChange={(e) => handleSettingChange('email', e.target.value)}
                        className="bg-white/5 border-white/20 text-white"
                      />
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="bio" className="text-white">Bio</Label>
                    <Textarea
                      id="bio"
                      value={settings.bio}
                      onChange={(e) => handleSettingChange('bio', e.target.value)}
                      placeholder="Tell us about yourself..."
                      className="bg-white/5 border-white/20 text-white resize-none"
                      rows={4}
                    />
                  </div>

                  <div className="flex items-center justify-between">
                    <div>
                      <Label className="text-white">Profile Visibility</Label>
                      <p className="text-sm text-gray-400">Control who can see your profile</p>
                    </div>
                    <Select
                      value={settings.profileVisibility}
                      onValueChange={(value: 'public' | 'private') => handleSettingChange('profileVisibility', value)}
                    >
                      <SelectTrigger className="w-32 bg-white/5 border-white/20 text-white">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent className="bg-black/80 border-white/10">
                        <SelectItem value="public">Public</SelectItem>
                        <SelectItem value="private">Private</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            {/* Appearance Settings */}
            <TabsContent value="appearance" className="space-y-6">
              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader>
                  <CardTitle className="text-white flex items-center gap-2">
                    <Palette className="w-5 h-5 text-purple-400" />
                    Theme & Display
                  </CardTitle>
                  <CardDescription className="text-gray-400">
                    Customize the visual appearance of your workspace
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                  <div>
                    <Label className="text-white mb-3 block">Theme</Label>
                    <div className="flex gap-3">
                      {[
                        { value: 'light', label: 'Light', icon: Sun },
                        { value: 'dark', label: 'Dark', icon: Moon },
                        { value: 'auto', label: 'Auto', icon: Monitor }
                      ].map((theme) => {
                        const Icon = theme.icon;
                        return (
                          <Button
                            key={theme.value}
                            variant={settings.theme === theme.value ? "default" : "outline"}
                            onClick={() => handleSettingChange('theme', theme.value)}
                            className={
                              settings.theme === theme.value
                                ? "bg-gradient-to-r from-purple-600 to-pink-600 text-white"
                                : "border-white/20 text-white hover:bg-white/10"
                            }
                          >
                            <Icon className="w-4 h-4 mr-2" />
                            {theme.label}
                          </Button>
                        );
                      })}
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <Label className="text-white">Font Family</Label>
                      <Select
                        value={settings.fontFamily}
                        onValueChange={(value) => handleSettingChange('fontFamily', value)}
                      >
                        <SelectTrigger className="bg-white/5 border-white/20 text-white">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent className="bg-black/80 border-white/10">
                          {FONT_FAMILIES.map((font) => (
                            <SelectItem key={font.value} value={font.value}>
                              {font.label}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label className="text-white">Font Size</Label>
                      <Select
                        value={settings.fontSize.toString()}
                        onValueChange={(value) => handleSettingChange('fontSize', parseInt(value))}
                      >
                        <SelectTrigger className="bg-white/5 border-white/20 text-white">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent className="bg-black/80 border-white/10">
                          {FONT_SIZES.map((size) => (
                            <SelectItem key={size} value={size.toString()}>
                              {size}px
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label className="text-white">Line Height</Label>
                      <Select
                        value={settings.lineHeight.toString()}
                        onValueChange={(value) => handleSettingChange('lineHeight', parseFloat(value))}
                      >
                        <SelectTrigger className="bg-white/5 border-white/20 text-white">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent className="bg-black/80 border-white/10">
                          {LINE_HEIGHTS.map((height) => (
                            <SelectItem key={height} value={height.toString()}>
                              {height}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            {/* Editor Settings */}
            <TabsContent value="editor" className="space-y-6">
              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader>
                  <CardTitle className="text-white flex items-center gap-2">
                    <Code className="w-5 h-5 text-green-400" />
                    Code Editor Preferences
                  </CardTitle>
                  <CardDescription className="text-gray-400">
                    Configure your coding environment and editor behavior
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <Label className="text-white">Tab Size</Label>
                      <div className="flex gap-2 mt-2">
                        {TAB_SIZES.map((size) => (
                          <Button
                            key={size}
                            variant={settings.tabSize === size ? "default" : "outline"}
                            size="sm"
                            onClick={() => handleSettingChange('tabSize', size)}
                            className={
                              settings.tabSize === size
                                ? "bg-green-600/20 text-green-300 border-green-500/30"
                                : "border-white/20 text-white hover:bg-white/10"
                            }
                          >
                            {size}
                          </Button>
                        ))}
                      </div>
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {[
                      { key: 'wordWrap', label: 'Word Wrap', description: 'Wrap long lines of code' },
                      { key: 'autoSave', label: 'Auto Save', description: 'Automatically save files as you type' },
                      { key: 'showLineNumbers', label: 'Line Numbers', description: 'Show line numbers in the editor' },
                      { key: 'showMinimap', label: 'Minimap', description: 'Show code minimap overview' },
                      { key: 'formatOnSave', label: 'Format on Save', description: 'Auto-format code when saving' },
                      { key: 'autoComplete', label: 'Auto Complete', description: 'Enable intelligent code completion' }
                    ].map((setting) => (
                      <div key={setting.key} className="flex items-center justify-between p-4 bg-white/5 rounded-lg border border-white/10">
                        <div>
                          <Label className="text-white">{setting.label}</Label>
                          <p className="text-sm text-gray-400">{setting.description}</p>
                        </div>
                        <Switch
                          checked={settings[setting.key as keyof UserSettings] as boolean}
                          onCheckedChange={(checked) => handleSettingChange(setting.key as keyof UserSettings, checked)}
                        />
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            {/* Security Settings */}
            <TabsContent value="security" className="space-y-6">
              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader>
                  <CardTitle className="text-white flex items-center gap-2">
                    <Shield className="w-5 h-5 text-red-400" />
                    Privacy & Security
                  </CardTitle>
                  <CardDescription className="text-gray-400">
                    Manage your account security and privacy settings
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                  <div className="space-y-4">
                    <div className="flex items-center justify-between p-4 bg-white/5 rounded-lg border border-white/10">
                      <div>
                        <Label className="text-white">Two-Factor Authentication</Label>
                        <p className="text-sm text-gray-400">Add an extra layer of security to your account</p>
                      </div>
                      <Switch
                        checked={settings.twoFactorEnabled}
                        onCheckedChange={(checked) => handleSettingChange('twoFactorEnabled', checked)}
                      />
                    </div>

                    <div className="flex items-center justify-between p-4 bg-white/5 rounded-lg border border-white/10">
                      <div>
                        <Label className="text-white">Allow Collaboration</Label>
                        <p className="text-sm text-gray-400">Let others collaborate on your projects</p>
                      </div>
                      <Switch
                        checked={settings.allowCollaboration}
                        onCheckedChange={(checked) => handleSettingChange('allowCollaboration', checked)}
                      />
                    </div>
                  </div>

                  <div className="p-4 bg-white/5 rounded-lg border border-white/10">
                    <Label className="text-white">API Key</Label>
                    <p className="text-sm text-gray-400 mb-3">Your personal API key for integrations</p>
                    <div className="flex gap-2">
                      <Input
                        type={showApiKey ? "text" : "password"}
                        value="sk-1234567890abcdef..."
                        readOnly
                        className="bg-white/5 border-white/20 text-white flex-1"
                      />
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setShowApiKey(!showApiKey)}
                        className="border-white/20 text-white hover:bg-white/10"
                      >
                        {showApiKey ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        className="border-white/20 text-white hover:bg-white/10"
                      >
                        <RefreshCw className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            {/* Notifications Settings */}
            <TabsContent value="notifications" className="space-y-6">
              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader>
                  <CardTitle className="text-white flex items-center gap-2">
                    <Bell className="w-5 h-5 text-yellow-400" />
                    Notification Preferences
                  </CardTitle>
                  <CardDescription className="text-gray-400">
                    Choose what notifications you want to receive
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  {[
                    { key: 'emailNotifications', label: 'Email Notifications', description: 'Receive notifications via email' },
                    { key: 'pushNotifications', label: 'Push Notifications', description: 'Receive browser push notifications' },
                    { key: 'projectUpdates', label: 'Project Updates', description: 'Notifications for project changes' },
                    { key: 'securityAlerts', label: 'Security Alerts', description: 'Important security notifications' }
                  ].map((setting) => (
                    <div key={setting.key} className="flex items-center justify-between p-4 bg-white/5 rounded-lg border border-white/10">
                      <div>
                        <Label className="text-white">{setting.label}</Label>
                        <p className="text-sm text-gray-400">{setting.description}</p>
                      </div>
                      <Switch
                        checked={settings[setting.key as keyof UserSettings] as boolean}
                        onCheckedChange={(checked) => handleSettingChange(setting.key as keyof UserSettings, checked)}
                      />
                    </div>
                  ))}
                </CardContent>
              </Card>
            </TabsContent>

            {/* Advanced Settings */}
            <TabsContent value="advanced" className="space-y-6">
              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader>
                  <CardTitle className="text-white flex items-center gap-2">
                    <Zap className="w-5 h-5 text-purple-400" />
                    Advanced Features
                  </CardTitle>
                  <CardDescription className="text-gray-400">
                    Experimental and advanced development features
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  {[
                    { key: 'aiAssistance', label: 'AI Code Assistance', description: 'Enable AI-powered code suggestions and help' },
                    { key: 'gitAutoCommit', label: 'Git Auto Commit', description: 'Automatically commit changes periodically' },
                    { key: 'debugMode', label: 'Debug Mode', description: 'Enable verbose logging and debug features' }
                  ].map((setting) => (
                    <div key={setting.key} className="flex items-center justify-between p-4 bg-white/5 rounded-lg border border-white/10">
                      <div>
                        <Label className="text-white">{setting.label}</Label>
                        <p className="text-sm text-gray-400">{setting.description}</p>
                      </div>
                      <Switch
                        checked={settings[setting.key as keyof UserSettings] as boolean}
                        onCheckedChange={(checked) => handleSettingChange(setting.key as keyof UserSettings, checked)}
                      />
                    </div>
                  ))}
                </CardContent>
              </Card>

              {/* Data Management */}
              <Card className="bg-black/40 backdrop-blur-xl border-white/10">
                <CardHeader>
                  <CardTitle className="text-white flex items-center gap-2">
                    <Database className="w-5 h-5 text-blue-400" />
                    Data Management
                  </CardTitle>
                  <CardDescription className="text-gray-400">
                    Export, import, or reset your data
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="flex flex-wrap gap-3">
                    <Button variant="outline" className="border-white/20 text-white hover:bg-white/10">
                      <Download className="w-4 h-4 mr-2" />
                      Export Data
                    </Button>
                    <Button variant="outline" className="border-white/20 text-white hover:bg-white/10">
                      <Upload className="w-4 h-4 mr-2" />
                      Import Data
                    </Button>
                    <Button variant="outline" className="border-red-500/30 text-red-400 hover:bg-red-500/10">
                      <Trash2 className="w-4 h-4 mr-2" />
                      Reset All Settings
                    </Button>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      </div>
    </div>
  );
}

